const BackOff = require("backo2");

const backoff = new BackOff({
    min: 1000,
    max: 1000 * 60,
    factor: 1.5,
    jitter: 0
});

let count = 0;
setInterval(()=>{
    let time = backoff.duration();
    count += time;
    console.log(backoff.attempts - 1, count / 1000, time / 1000);
}, 1000);
