const Distribute = require("../lib/distribute-util.js");
const topic = Distribute.getInstance("distribute-test");

topic.on("test", (data) => {
    console.log('receive data:', data);
});

topic.emit("test", {'test': 'start'});
let count = 0;
setInterval(() => {
    topic.emit("test", {'test': 'ttt' + (++count)});
}, 5000);
