'use strict';

const getAutoThemeManage = require('./lib/auto-theme-manage');

/**
 * 自动抽取皮肤的 less 扩展插件
 * @param opt {object}
 * @constructor
 */
const AutoThemePlugin = function (opt) {
    this.options = opt;
};

AutoThemePlugin.prototype = {
    install: function (less, pluginManager) {
        pluginManager.addVisitor(
            new (getAutoThemeManage(less, pluginManager))(this.options)
        );
    }
};

module.exports = AutoThemePlugin;
